// Modified timestamp handling to preserve the exact time
if (!msg.payload.timestamp_iso) {
    try {
        let exactTimestamp;

        // If timestamp is already in the payload and in the correct format
        if (msg.payload.timestamp instanceof Date) {
            exactTimestamp = msg.payload.timestamp;
        }
        // Handle string timestamp from payload
        else if (typeof msg.payload.timestamp === 'string') {
            if (msg.payload.timestamp.includes(':')) {
                // Parse the custom format "10:20:13 15 04 2025"
                let parts = msg.payload.timestamp.split(' ');
                if (parts.length >= 3) {
                    let timeStr = parts[0]; // "10:20:13"
                    let day = parts[1];     // "15"
                    let month = parts[2];   // "04"
                    let year = parts[3] || new Date().getFullYear(); // Use current year if not provided

                    // Create timestamp without timezone conversion
                    let dateString = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T${timeStr}`;

                    // Use the local string representation directly
                    exactTimestamp = new Date(dateString);

                    // Important: Adjust for any local timezone offset to keep exact time
                    // This prevents browser or Node.js from applying timezone shifts
                    let offset = exactTimestamp.getTimezoneOffset() * 60000; // offset in milliseconds
                    exactTimestamp = new Date(exactTimestamp.getTime() + offset);
                } else {
                    exactTimestamp = new Date();
                }
            } else {
                // Handle other string formats or timestamps
                exactTimestamp = new Date(msg.payload.timestamp);
            }
        } else {
            // Fallback to current time
            exactTimestamp = new Date();
        }

        // Use this exact timestamp for all chart messages

        // Parse numeric values
        msg.payload.ph = parseFloat(msg.payload.ph) || 0;
        msg.payload.tds = parseFloat(msg.payload.tds) || 0;
        msg.payload.turbidity = parseFloat(msg.payload.turbidity) || 0;
        msg.payload.temperature = parseFloat(msg.payload.temperature) || 0;

        // Create messages for charts with the exact timestamp
        var phChart = { 
            payload: msg.payload.ph, 
            topic: "pH", 
            timestamp: exactTimestamp 
        };
        var tdsChart = { 
            payload: msg.payload.tds, 
            topic: "TDS", 
            timestamp: exactTimestamp 
        };
        var turbidityChart = { 
            payload: msg.payload.turbidity, 
            topic: "Turbidity", 
            timestamp: exactTimestamp 
        };
        var temperatureChart = { 
            payload: msg.payload.temperature, 
            topic: "Temperature", 
            timestamp: exactTimestamp 
        };

        // Create separate messages for gauges (no timestamp needed)
        var ph = { payload: msg.payload.ph, topic: "pH" };
        var tds = { payload: msg.payload.tds, topic: "TDS" };
        var turbidity = { payload: msg.payload.turbidity, topic: "Turbidity" };
        var temperature = { payload: msg.payload.temperature, topic: "Temperature" };

        // Create messages for status indicators
        var phStatus = { payload: getPhStatus(msg.payload.ph) };
        var tdsStatus = { payload: getTdsStatus(msg.payload.tds) };
        var turbidityStatus = { payload: getTurbidityStatus(msg.payload.turbidity) };
        var tempStatus = { payload: getTemperatureStatus(msg.payload.temperature) };

        // Create a copy of the original message with timestamp_iso for device info
        var deviceInfoMsg = { payload: { ...msg.payload, timestamp_iso: exactTimestamp.toISOString() } };

        // Return all messages
        return [phChart, tdsChart, turbidityChart, temperatureChart, 
                ph, tds, turbidity, temperature, 
                phStatus, tdsStatus, turbidityStatus, tempStatus, 
                deviceInfoMsg];
    } catch (e) {
        // Handle errors
        node.error("Error processing timestamp: " + e.message);
        return null;
    }
}

// Helper functions for status determination
function getPhStatus(value) {
    if (value >= 6.5 && value <= 8.5) return "Normal";
    else if (value < 6.5) return "Acidic";
    else return "Alkaline";
}

function getTdsStatus(value) {
    if (value < 300) return "Excellent";
    else if (value >= 300 && value <= 600) return "Good";
    else if (value > 600 && value <= 900) return "Fair";
    else return "Poor";
}

function getTurbidityStatus(value) {
    if (value < 5) return "Clear";
    else if (value >= 5 && value <= 10) return "Slightly Cloudy";
    else return "Cloudy";
}

function getTemperatureStatus(value) {
    if (value >= 20 && value <= 30) return "Optimal";
    else if (value < 20) return "Cold";
    else return "Hot";
}

// Return all messages in the correct order
return [phChart, tdsChart, turbidityChart, temperatureChart, 
        ph, tds, turbidity, temperature, 
        phStatus, tdsStatus, turbidityStatus, tempStatus, 
        deviceInfoMsg];

// Simple Water Quality Index calculation
var ph = msg.payload.ph;
var tds = msg.payload.tds;
var turbidity = msg.payload.turbidity;
var temperature = msg.payload.temperature;

// Normalize values to 0-100 scale
var phScore = calculatePhScore(ph);
var tdsScore = calculateTdsScore(tds);
var turbidityScore = calculateTurbidityScore(turbidity);
var tempScore = calculateTempScore(temperature);

// Calculate overall WQI (simple average)
var wqi = (phScore + tdsScore + turbidityScore + tempScore) / 4;

// Quality classification
var quality = "";
if (wqi >= 90) quality = "Excellent";
else if (wqi >= 70) quality = "Good";
else if (wqi >= 50) quality = "Fair";
else if (wqi >= 25) quality = "Poor";
else quality = "Very Poor";

// Helper functions for parameter scoring
function calculatePhScore(value) {
    if (value >= 6.5 && value <= 7.5) return 100;
    else if (value >= 6.0 && value < 6.5) return 80;
    else if (value > 7.5 && value <= 8.5) return 80;
    else if (value >= 5.0 && value < 6.0) return 60;
    else if (value > 8.5 && value <= 9.0) return 60;
    else return 30;
}

function calculateTdsScore(value) {
    if (value < 300) return 100;
    else if (value >= 300 && value <= 600) return 80;
    else if (value > 600 && value <= 900) return 60;
    else if (value > 900 && value <= 1200) return 40;
    else return 20;
}

function calculateTurbidityScore(value) {
    if (value < 1) return 100;
    else if (value >= 1 && value < 5) return 80;
    else if (value >= 5 && value <= 10) return 60;
    else if (value > 10 && value <= 15) return 40;
    else return 20;
}

function calculateTempScore(value) {
    if (value >= 20 && value <= 30) return 100;
    else if ((value >= 15 && value < 20) || (value > 30 && value <= 35)) return 80;
    else if ((value >= 10 && value < 15) || (value > 35 && value <= 40)) return 60;
    else return 40;
}

// Create messages for WQI gauge and status
var wqiGauge = { payload: Math.round(wqi), topic: "WQI" };
var wqiStatus = { payload: quality };

return [wqiGauge, wqiStatus];
